import { useEffect, useState } from "react";
import Card from "../card/Card";
import style from "./Weatherapp.module.css"
const Weather = () => {
     const [search,setSearch] =useState()
     const [data,setData]=useState();



     const searchHandler=(e)=>{
        setSearch(e.target.value)
     }

     useEffect(()=>{
         fetch('https://https://api.openweathermap.org/data/2.5/weather?q=${}&appid={9588644109b80cfb183de50f9520f181}')
         .then((res)=>res.json())
         .then((data)=>{})
     })


       //Current date day time
       const days=["sunday","monday","Tuesday","wednesday","Thrusday","Friday","Saturday"]
       const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
       const d = new Date();
       const date=d.getDate()
       const day= days[d.getDay()];
       const month= months[d.getMonth()];
       const year =d.getFullYear();
     
    return (
        <>
            <div className={style.container}>
                <div>
                    <h1>Weather App</h1>
                    <input type="text" value={search} onChange={searchHandler}  placeholder="city name" />
                </div>
                <Card  day={day} date={date} month={month} year={year} />
            </div>

        </>
    )
}
export default Weather;
